import React, { useEffect, useRef } from 'react';
import '../css/pages/home.scss';

function Home() {
  const h1Ref = useRef(null);
  const pRef = useRef(null);

  function typeEffect(element, speed) {
    const text = element.innerHTML;
    element.innerHTML = "";

    let i = 0;
    const timer = setInterval(() => {
      if (i < text.length) {
        element.append(text.charAt(i));
        i++;
      } else {
        clearInterval(timer);
      }
    }, speed);
  }

  useEffect(() => {
    const speed = 75;
    const h1 = h1Ref.current;
    const p = pRef.current;
    const delay = h1.innerHTML.length * speed + speed;

    typeEffect(h1, speed);

    setTimeout(() => {
      p.style.display = "inline-block";
      typeEffect(p, speed);
    }, delay);
  }, []);

  return (
    <div className='home-container'>
      <div className="text">
        <div id="table">
          <div id="centeralign">
            <h1 ref={h1Ref}>Bienvenue sur mon site !</h1>
            <p ref={pRef}>
              Vous Ãªtes actuellement dans le menue principal. Pour en savoir plus sur moi, veuillez naviguer sur le menu de votre choix.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
